USE mariners_2022;

DROP TABLE IF EXISTS game_log;
CREATE TABLE game_log(
   gameID         INTEGER  NOT NULL PRIMARY KEY 
  ,gameDate       DATE  NOT NULL
  ,location       VARCHAR(4) NOT NULL
  ,opponent       VARCHAR(5) NOT NULL
  ,outcome        VARCHAR(1) NOT NULL
  ,marinersScore  INTEGER  NOT NULL
  ,opponentScore  INTEGER  NOT NULL
  ,teamRecord     VARCHAR(6) NOT NULL
  ,winningPitcher VARCHAR(11) NOT NULL
  ,losingPitcher  VARCHAR(17) NOT NULL
  ,save           VARCHAR(12)
  ,attendance     INTEGER  NOT NULL
  ,time           VARCHAR(4) NOT NULL
  ,timeOfDay      VARCHAR(5) NOT NULL
);

SELECT * FROM game_log;
